        let pune13 = [CityData(name: "–ú–∏–∫–æ–ª–∞"), CityData(name: "–í–ª–∞—Å–Ω–∏–π –ø—Ä–æ–µ–∫—Ç —Å—Ç—É–¥–µ–Ω—Ç–∞ —ñ–∑ –õ—É—Ü—å–∫—É.\n–ù–∞–∑–≤–∞ BaWN —Ü–µ –∞–±—Ä–µ–≤—ñ–∞—Ç—É—Ä–∞ –≤—ñ–¥ Black and White Night. –°—Ç–∏–ª—å –ø—ñ—Å–µ–Ω—å - –ø–µ—Ä–µ–≤–∞–∂–Ω–æ –ø–æ—Å—Ç–ø–∞–Ω–∫.")]
